import React from 'react';
import Header from '../components/Header';
import getMusics from '../services/musicsAPI';
import Carregando from '../Carregando';

class Album extends React.Component {
  constructor() {
    super();
    this.requestMusic = this.requestMusic.bind(this);
    this.state = {
      loading: false,
      tracks: [],
    };
  }

  componentDidMount() {
    this.requestMusic();
  }

  async requestMusic() {
    this.setState({ loading: true });
    const URL = window.location.href;
    const id = URL.split('album/');
    const musics = await getMusics(id[1]);
    this.setState({ loading: false, tracks: musics });
    console.log(musics);
    return musics;
  }

  render() {
    const { loading, tracks } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        { loading ? <Carregando /> : '' }
        <h4>{ tracks.artistName }</h4>
        <h5>{ tracks.collectionName }</h5>
        {
          tracks.map((music, index) => (
            <section key={ music.amgArtistId }>
              <span>
                Music
                {' '}
                { `${index + 1}` }
              </span>
              <br />
              <audio
                data-testid="audio-component"
                src={ music.collectionViewUrl }
                controls
              >
                <track kind="captions" />
                O seu navegador n√£o suporta o elemento
                <code>
                  {' '}
                  { music.artistViewUrl }
                  {' '}
                </code>
                .
              </audio>
            </section>

          ))
        }
      </div>
    );
  }
}

