import React from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Carregando from '../Carregando';

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      showButton: false,
      loading: false,
      requestApi: false,
      artist: '',
      albuns: [],
    };
    this.searchButton = this.searchButton.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.showDiv = this.showDiv.bind(this);
    this.controlObject = this.controlObject.bind(this);
  }

  async handleClick() {
    const inputValue = document.getElementsByTagName('input')[0];
    this.setState({ loading: true, artist: inputValue.value });
    const response = await searchAlbumsAPI(inputValue.value);
    this.setState({ showButton: false, loading: false, requestApi: true });
    this.controlObject(response);
    inputValue.value = '';
  }

  controlObject(objectName) {
    this.setState({ albuns: objectName });
  }

  searchButton(event) {
    const caracters = event.target.value;
    const LIMIT_NUM = 2;
    if (caracters.length >= LIMIT_NUM) {
      this.setState({ showButton: true });
    } else {
      this.setState({ showButton: false });
    }
  }

  showDiv() {
    const { requestApi } = this.state;
    if (requestApi) {
      return false;
    }
    return true;
  }

  render() {
    const { showButton, loading, requestApi, artist, albuns } = this.state;
    return (
      <div data-testid="page-search">
        <Header />
        {
          loading
            ? <Carregando />
            : (
              <form>
                <input
                  type="text"
                  data-testid="search-artist-input"
                  name="search-artist-input"
                  placeholder="Nome do Artista"
                  onChange={ this.searchButton }
                />
                <button
                  type="button"
                  data-testid="search-artist-button"
                  disabled={ !showButton }
                  onClick={ this.handleClick }
                >
                  Pesquisar
                </button>
              </form>
            )
        }

        <div>
          {
            requestApi
              ? (
                <span>
                  Resultado de Ã¡lbuns de:
                  {' '}
                  { artist }
                </span>)
              : ''
          }
        </div>
      </div>
    );
  }
}

export default Search;
