import React from 'react';
import Header from '../components/Header';
import getMusics from '../services/musicsAPI';
import Carregando from '../Carregando';

class Album extends React.Component {
  constructor() {
    super();
    this.requestMusic = this.requestMusic.bind(this);
    this.queryAlbunInfo = this.queryAlbunInfo(this);
    this.state = {
      loading: false,
    };
  }

  componentDidMount() {
    this.requestMusic();
  }

  async requestMusic() {
    this.setState({ loading: true });
    const URL = window.location.href;
    const id = URL.split('album/');
    const musics = await getMusics(id[1]);
    this.setState({ loading: false });
    console.log(musics);
    this.queryAlbunInfo(music);
    return musics;
  }

  queryAlbunInfo(obj) {
    const objToArr = Object.entries(obj);
    const { artistName, collectionName } = objToArr;
    localStorage.setItem('artist', artistName);
    localStorage.setItem('album', collectionName);
  }

  render() {
    const { loading } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        { loading ? <Carregando /> : '' }
        <p>Album</p>
      </div>
    );
  }
}

export default Album;
