import React from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Carregando from '../Carregando';

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      showButton: false,
      loading: false,
    };
    this.searchButton = this.searchButton.bind(this);
    this.handleClick = this.handleClick.bind(this);
  }

  async handleClick() {
    const inputValue = document.getElementsByTagName('input')[0];
    this.setState({ loading: true });
    await searchAlbumsAPI(inputValue.value);
    inputValue.value = '';
    this.setState({ showButton: false, loading: false });
  }

  searchButton(event) {
    const caracters = event.target.value;
    const LIMIT_NUM = 2;
    if (caracters.length >= LIMIT_NUM) {
      this.setState({ showButton: true });
    } else {
      this.setState({ showButton: false });
    }
  }

  render() {
    const { showButton, loading } = this.state;
    return (
      <div data-testid="page-search">
        <Header />
        {
          loading
            ? <Carregando />
            : (
              <form>
                <input
                  type="text"
                  data-testid="search-artist-input"
                  name="search-artist-input"
                  placeholder="Nome do Artista"
                  onChange={ this.searchButton }
                />
                <button
                  type="button"
                  data-testid="search-artist-button"
                  disabled={ !showButton }
                  onClick={ this.handleClick }
                >
                  Pesquisar
                </button>
              </form>
            )
        }

      </div>
    );
  }
}

export default Search;
